function query() {
    return gTtravels;
}

function getNextId(id) {
    const currIdx = gTtravels.findIndex(travel => travel.id === +id);
    if(currIdx === gTtravels.length-1 ){
        return gTtravels[0].id
    } else{
        return gTtravels[currIdx+1].id
    }
}

function getPrevId(id) {
    const currIdx =  gTtravels.findIndex(travel => travel.id === +id);
    if(currIdx === 0){
        return gTtravels[gTtravels.length-1].id
    } else{
        return gTtravels[currIdx-1].id
    }
}

function getById(id) {
    return gTtravels.find(travel => travel.id === +id);
}

function saveTravel(travelToSave) {
    if (travelToSave.id) {
        gTtravels = gTtravels.map(travel => {
            if (travel.id === travelToSave.id) {
                travel = travelToSave;
            }
            return travel;
        })
    } else {
        travelToSave.id = _makeId();
        gTtravels.push = travelToSave;
    }
    localStorage.setItem(TRAVELS, JSON.stringify(gTtravels))
}

function remvoeTravel(id) {
    gTtravels = gTtravels.filter(travel => travel.id !== id);
    localStorage.setItem(TRAVELS, JSON.stringify(gTtravels))
}

function _makeId(length = 6) {
    var txt = '';
    var possible = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    for (var i = 0; i < length; i++) {
        txt += possible.charAt(Math.floor(Math.random() * possible.length));
    }
    return txt;
}